extBlock <>	;ROM boot task -> RAM*Bt task.bootBufferFull		Condition	<>	;Condition variable for						;handler - bootstrap						;coordination.jumpTable		BootJumpTable	<>	;Use to do indirect jumps.bootDeviceIORSpace	DB	200 DUP (?)	;This section is used by the						;device boot heads for whatever						;needs they may have. i.e. One						;could define device specific						;structures in the device boot						;head then load a register with						;"deviceSpecificArea" and use						;.extension to access the needed						;variables.							;---------------------------------------------------------------------------------;everything above is identical with IORRAMBt.asm;---------------------------------------------------------------------------------ORG OFFSET bootDeviceIORSpace			;Overlay since icon data structures						;will not be used once boot device is						;determined.					bootDeviceICON		EQU	$bootDevice1ICON		ICONDataStructure	<>bootDevice2ICON		ICONDataStructure	<>bootDevice3ICON		ICONDataStructure	<>bootDevice4ICON		ICONDataStructure	<>bootDevice5ICON		ICONDataStructure	<>bootDevice6ICON		ICONDataStructure	<>bootDevice7ICON		ICONDataStructure	<>bootDevice8ICON		ICONDataStructure	<>bootDevice9ICON		ICONDataStructure	<>bootDevice10ICON	ICONDataStructure	<>displayWidthInBytes	DW	?		displaySegment		DW	?	;These are used to remember where the displayOffset		DW	?	; icons go in display memory.selectedICON		DW	?		firstICONOffset		DW	?		ICONsBottomEdge		DW	?		ICONsTopEdge		DW	?		displayStartOffset	DW	?	;These are used to remember the beginningdisplayStartSegment	DW	?	; of the display.	bigTimeOut		DB	?	;Used to count down seconds in timeout.littleTimeOut		DW	?	;Used to compare to interval timer to see if one second has passed.allowTimeout		DB	?	;Flag to control if default booting will occur.;currentCursor		cursorPosition	<>EndBootstrapIOR		EQU	$OverlayLength		EQU	$ - bootDeviceIORSpaceBootstrapIOR		ENDS			END