	0FH; Defs for dirty bitsMesaReadBits			EQU	0MesaNotReadBits			EQU	0FHBytesInDirtyBitPackedArray	EQU	8H	masterDirtySet			EQU	80H;pDisplayBase equ'ed to 100h + 60h = 160h in pcedefs.asm;pDisplayBase = B0000; pDisplayBase1 = B4000; pDisplayBase2 = B8000 etc.pDisplayBase1			EQU	pDisplayBase - 20HpDisplayBase2			EQU	pDisplayBase1 - 20HpDisplayBase3			EQU	pDisplayBase2 - 20HMonoDBBytesToLoad		EQU	2HColorDBBytesToLoad		EQU	7H ; 8 bytes in p array but most need 7; Display hardware configurationNoDisplay			EQU	1HColorGraphics40Display		EQU	2HColorgraphics80Display		EQU	4HMonochromeOrMore		EQU	8H; Mono mode defsRegMonoChrome			EQU	29HMonoChromeNoBlinking		EQU	09H; I/O Test Bits	IOInToPC			EQU	1hIOOutFromPC			EQU	0h; Various Junk data to maintain accurate hardware emulationJunk0				EQU	0hJunkFF				EQU	0FFhJunk80				EQU	080h; 9 words per struc Mono6845RegistersStruc		STRUCMR00				DB	?MR01				DB	?MR02				DB	?MR03				DB	?MR04				DB	?MR05				DB	?MR06				DB	?MR07				DB	?MR08				DB	?MR09				DB	?; MonoCursorShapeMR0A				DB	?MR0B				DB	?; MonoPageStartAddrMR0C				DB	?MR0D				DB	?; MonoCursorAddrMR0E				DB	?MR0F				DB	?MR10				DB	?MR11				DB	?Mono6845RegistersStruc		ENDSMono6845RegistersLength		EQU	SIZE Mono6845RegistersStrucColor6845RegistersStruc		STRUCCR00				DB	?CR01				DB	?CR02				DB	?CR03				DB	?CR04				DB	?CR05				DB	?CR06				DB	?CR07				DB	?CR08				DB	?CR09				DB	?; ColorCursorShapeCR0A				DB	?CR0B				DB	?; ColorPageStartAddrCR0C				DB	?CR0D				DB	?; ColorCursorAddrCR0E				DB	?CR0F				DB	?CR10				DB	?CR11				DB	?Color6845RegistersStruc		ENDSColor6845RegistersLength	EQU	SIZE Color6845RegistersStruc