rootPageHeaderSize	EQU	16	; 16 bytes of header before 1st file IDdiskShapeCylinder	EQU	0	; location of disk shapediskShapeHeadAndSector	EQU	14	;  is cyl. 0, head 0, sector 14diskShapeSeal		EQU	9665H	; byteswapped 6596H = 62626 octaldiskShapeVersion	EQU	100H	; byteswapped 1;--			END	DskBDefs