+31eePromPCEMemSize	EQU	RAMSegment+wordEEPromOffset+32eePromPCEConfig	EQU	RAMSegment+wordEEPromOffset+34eePromOption1		EQU	RAMSegment+wordEEPromOffset+36eePromOption2		EQU	RAMSegment+wordEEPromOffset+38eePromOption3		EQU	RAMSegment+wordEEPromOffset+40;this is actually defined in ROMEEP.asm.;eePromFloppy		EQU	ROMSegment+wordEEPromOffset+42;eePromSpare2		EQU	RAMSegment+byteEEPromOffset+44;eePromSpare3		EQU	RAMSegment+byteEEPromOffset+45;eePromSpare4		EQU	RAMSegment+byteEEPromOffset+46;eePromSpare5		EQU	RAMSegment+byteEEPromOffset+47;eePromSpare6		EQU	RAMSegment+byteEEPromOffset+48;eePromSpare7		EQU	RAMSegment+byteEEPromOffset+49eePromDiagAutoRun	EQU	RAMSegment+wordEEPromOffset+44eePromRDCLandingZone	EQU	RAMSegment+wordEEPromOffset+46eePromWriteCount	EQU	RAMSegment+wordEEPromOffset+48eePromSpare8		EQU	RAMSegment+byteEEPromOffset+50eePromSpare9		EQU	RAMSegment+byteEEPromOffset+51eePromSpare10		EQU	RAMSegment+byteEEPromOffset+52eePromSpare11		EQU	RAMSegment+byteEEPromOffset+53eePromSpare12		EQU	RAMSegment+byteEEPromOffset+54eePromSpare13		EQU	RAMSegment+byteEEPromOffset+55eePromSpare14		EQU	RAMSegment+byteEEPromOffset+56eePromSpare15		EQU	RAMSegment+byteEEPromOffset+57eePromSpare16		EQU	RAMSegment+byteEEPromOffset+58eePromSpare17		EQU	RAMSegment+byteEEPromOffset+59eePromSpare18		EQU	RAMSegment+byteEEPromOffset+60eePromSpare19		EQU	RAMSegment+byteEEPromOffset+61eePromSpare20		EQU	RAMSegment+byteEEPromOffset+62eePromSpare21		EQU	RAMSegment+byteEEPromOffset+63eePromSpare22		EQU	RAMSegment+byteEEPromOffset+64eePromSpare23		EQU	RAMSegment+byteEEPromOffset+65eePromSpare24		EQU	RAMSegment+byteEEPromOffset+66eePromSpare25		EQU	RAMSegment+byteEEPromOffset+67