arRxEnable		EQU	0FEHrxCharLength		EQU	0C0HrxChar7Bits		EQU	7FHrxChar6Bits		EQU	3FHrxChar5Bits		EQU	1FHi8274AutoEnable		EQU	020H	;-- Write Register 4 descriptioni8274x16Clock		EQU	040hi8274StopBits2		EQU	00Chi8274StopBits1		EQU	004hi8274OddParity		EQU	001hi8274EvenParity		EQU	003h;-- Write Register 5 descriptioni8274Tx8BitChar		EQU	060hi8274Tx7BitChar		EQU	020hi8274TxEnable		EQU	008hclearTxEnable		EQU	0F7Hi8274Dtr		EQU	080hi8274Rts		EQU	002hi8274ClearDtr		EQU	7FH;;-- Read Register 0 descriptionserialTxReady		EQU	04h	;Tx buffer emptyi8274SerialCDetect	EQU	08h	;Carrier detectserialCTS		EQU	20h	;Clear to SendserialTxUnderrun	EQU	40h	;Tx Underrun serialBreak		EQU	80h	;Break detectedi8274RxReady		EQU	001hi8274TxBufferEmpty	EQU	004hi8274ChanReset		EQU	018hi8274ErrorReset		EQU	030hi8274IntPending		EQU	002h;-- Read Register 1 descriptionserialParityError	EQU	10h	;Parity error statusserialRxOverrun		EQU	20h	;Rx overrun errorserialFrameError	EQU	40h	;CRC/Framing errorserialEndOfFrame	EQU	80h	;End of Frame in SDLC mode; BaudRateTable:; Constants to load into i8254 Counter in order to generate baud rates for the ; i8274 MPSC.  Input freq to i8254 counter is 4 MHz, set up 8274 to divide its ; clock input by 16. ( 4 MHz / 208 / 16 = 1200 baud rate ).;--------------------------------------------------------------------------------Baud50		EQU		5000Baud75		EQU		3334Baud110		EQU		2272Baud150		EQU		1667Baud300		EQU		833Baud600		EQU		417Baud1200	EQU		208Baud1800	EQU		138Baud2000	EQU		126Baud2400	EQU		104Baud3600	EQU		69Baud4800	EQU		52Baud7200	EQU		35Baud9600	EQU		26