H;	Line number of cursor position (Low byte)CursorLinePortHighDayBreak	EQU   0EC86H;	Line number of cursor position (High byte);DMCWordsPerLineDayBreak		EQU   0EC88H;	DMC register that contains number of words per line;DMCDisplStrtAddrLowDayBreak	EQU   0EC89H;	DMC register that holds low byte of display mem. strt addr.DMCDisplStrtAddrHighDayBreak	EQU   0EC8AH;	DMC reg. that holds high byte of display mem. strt addr.;DisplMemStatReg1stDayBreak	EQU   0EC8CH       SysMemStatReg2ndDayBreak	EQU   0EC8DHSysMemStatReg3rdDayBreak	EQU   0EC8EHSysMemStatReg4thDayBreak	EQU   0EC8FH;DisplaySoftReset		EQU   0ED60H;	A read from this port resets display HWCursorBufferPortsDayBreak	EQU   0ED00H;	Cursor buffer starting locn.;;; - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - -;;DAISY ADDRESSES -- All these I/O addresses are TBD;;Daisy A chip registers are accessed via IN/OUT instructions and their addresses are calcurate from following equation.;	0800 + 0100 * AID + Location;		here AID is the A chip ID: CARDINAL [0..4);following definitions are Location.;in actual use, the AID should be ORed into upper byte.;AID0 = 0800H;AID1 = 0900H;AID2 = 0A00H;AID3 = 0B00HAChip0			EQU	08H;CursorBufferReg		EQU	880H;	Cursor buffer starting location.HCursor			EQU	8A0H; 	Cursor x-coodinate in words. 				    ;	b15,,b14 = Cursor Nibble Offset, b13,,b12 = Cursor Rotate parameterHBorder1		EQU	8A4H;	Starting point of display bitmap.				    ;	b15 = DCReset, b14 = ColorMode', b13 = DCTest', b12 = PICyBDR'HBorder0		EQU	8A6H;	End point of display bitmap.				    ;	b15,,14 = HSyncOff1-0, b13 = Interlace, b12 = BaseP10HBlank0			EQU	8A8H;	Start left border location. (b15,,b12 = unused).HBlank1			EQU	8AAH;	Stop right border and start blank location.HSync1			EQU	8ACH;	Start Horiz Sync.HSync0			EQU	8AEH;	Stop Horiz Sync.VCursor			EQU	8B0H;	Cursor y-coodinate in words.				    ;	b15,,b12 = Mix RuleVInt			EQU	8B2H;	Vertical interrupt point.VBorder1		EQU	8B4H;	Start lower border location.VBorder0		EQU	8B6H;	Start display bitmap.VBlank1			EQU	8B8H;	End of lower border location.VBlank0			EQU	8BAH;	Start upper border location.VSync0			EQU	8BCH;	End of VSyncVSync1			EQU	8BEH;	Start of VSyncBaseP			EQU	8C0H;	page address of start of bitmap.QRandLR			EQU	8C2H;	Line count (b15,,b7) and num of quad words (b5,,b0)Border1			EQU	8C4H;	border pattern for even line.Border0			EQU	8C6H;	border pattern for odd line.;-----------------------------------------------------------------------------------------------------------;;Constants used for  initialization;NumHorzBytes		EQU	0AH;	Number of values in horizontal control storeNumVertBytes		EQU	09H;	Number of values in vertical control store;DefaultMixRule		EQU	0E0H;	Default value for Cursor/Data mix (Daisy)DCResetBit			EQU	8000H;;;----------------------------------------------------------------------------------------------------------;;Misc;NumBitMapBytes			EQU	32;	Number of bytes in cursor patternNumBitMapBytesDaisy		EQU	16; Number of words in cursor pattern.;; Flags in chngdInfo wordcursorPositionChngd		EQU	8000H	;b15 = change in cursor positioncursorPatternChngd		EQU	4000H	;b14 = change in cursor patternborderPatternChngd		EQU	2000H	;b13 = change in border patternbackgroundChngd			EQU	1000H	;b12 = change in backgrondcommandChngd			EQU	0800H	;b11 = change in command to display control register;;constatns to determine displCntl contents for Daisy.pictureBorderCheckMask	EQU	08HborderOnlyMask		EQU	10HvideoDisableCheckMask	EQU	02HvideoDisableMask	EQU	00HMSNibbleMask		EQU	0FFFH;----------------------------------------------------------------------------------------------------------;